import React, { useState, useContext } from 'react';
import AlertContext from '../../contexts/alert/alertContext';
import AuthContext from '../../contexts/auth/authContext';


const Register = () =>{
	const alertContext = useContext(AlertContext);
	const authContext = useContext(AuthContext);

	const { setAlert } = alertContext;
	const { Register } = authContext;

	const [ user, setUser] = useState({
		name: '',
		email:'',
		password: '',
		password_confirm: ''
	});
	const { name, email, password, password_confirm} = user;

	const onChange = e => setUser({...user, [e.target.name]: e.target.value});
	const onSubmit = e => {
		e.preventDefault();
		if( name === '' || email === '' || password === ''){
			setAlert('Please enter all fields', 'danger')
		}else if (password !== password_confirm){	
			setAlert('Passwords do not match', 'danger')
		}else{
			register({
				name,
				email,
				password
			});
		}

	};


	return(
		<div className='form-container'>
			<h1>Account <span className='text-primary'>Register</span></h1>
			<form onSubmit={onSubmit}>
				<div className='form-group'>
					<label htmlFor='name'>Name</label>
					<input type='text' name='name' value={name} onChange={onChange} />
				</div>
				<div className='form-group'>
					<label htmlFor='email'>Email</label>
					<input type='email' name='email' value={email} onChange={onChange} />
				</div>
				<div className='form-group'>
					<label htmlFor='password'>Password</label>
					<input type='password' name='password' value={password} onChange={onChange} required minLength='8' />
				</div>
				<div className='form-group'>
					<label htmlFor='password_confirm'>Confirm Password</label>
					<input type='password' name='password_confirm' value={password_confirm} onChange={onChange} required minLength='8' />
				</div>
				<input type='submit' value='Register' className='btn btn-primary btn-block' />
			</form>
		</div>
	)
}

export default Register;
